(524,'\'\'\'By default Node application allow CORS for all, means,\'\'\' \'\'<big>Access-Control-Allow-Origin: *</big>\'\'\n\n\'\'So to hide this we have to add the following in proxy_pass section of NginX\'\'\n<pre> proxy_hide_header \'Access-Control-Allow-Origin\'; </pre>\n\n\'\'Also in location section of NginX we need to add custom origin\'\'\n<pre>add_header \'Access-Control-Allow-Origin\' \"https://test.ril.com\" always;</pre>\n\n<u>\'\'Complete block looks like\'\'</u>\n<pre>\nlocation / {\nset $cors \'true\';\nif ($cors = \'true\') {\n        add_header \'Access-Control-Allow-Origin\' $http_origin always;\n        add_header \'Access-Control-Allow-Credentials\' \'true\' always;\n        add_header \'Access-Control-Allow-Methods\' \'GET, POST, PUT, DELETE, OPTIONS\' always;\n        add_header \'Access-Control-Allow-Headers\' \'Accept,Authorization,Cache-Control,Content-Type,DNT,If-Modified-Since,Keep-Alive,Origin,User-Agent,X-Requested-With\' always;\n     }\n\nif ($request_method = \'OPTIONS\') {\n        # Tell client that this pre-flight info is valid for 20 days\n        add_header \'Access-Control-Max-Age\' 1728000;\n        add_header \'Content-Type\' \'text/plain charset=UTF-8\';\n        add_header \'Content-Length\' 0;\n        return 204;\n     }\n        proxy_http_version 1.1;\n        proxy_set_header Upgrade $http_upgrade;\n        proxy_set_header Connection \"upgrade\";\n        proxy_hide_header \'Access-Control-Allow-Origin\';\n        proxy_pass http://10.129.65.31:31111;\n }\n</pre>','utf-8'),
