(737,'*\'\'\'<u>For Redis, Elasticsearch, Logstash, Grafana</u>\'\'\'\n\n<pre>\nversion: \"3.2\"\nservices:\n\n redis:\n    container_name: redis01\n    image: \"docker.rril.ril.com/wcs/wcs-redis-server:latest\"\n    ports:\n     - \"6379:6379\"\n    volumes:\n     - /data01/rediscache:/var/lib/redis\n    environment:\n     - TZ=Asia/Kolkata\n     - REDIS_REPLICATION_MODE=master\n     - ALLOW_EMPTY_PASSWORD=yes\n    restart: always\n    networks:\n      node_net:\n        ipv4_address: 172.28.1.2\n\n elasticsearch:\n    container_name: elasticsearch01\n    image: docker.rril.ril.com/elasticsearch:7.7.1\n    ports:\n     - 9200:9200\n    volumes:\n     - ./elasticsearch.yml:/usr/share/elasticsearch/config/elasticsearch.yml\n     - /mnt/elastic/data:/var/lib/elasticsearch\n     - /mnt/elastic/log:/var/log/elasticsearch\n    restart: always\n    networks:\n      node_net:\n        ipv4_address: 172.28.1.3\n\n logstash:\n    container_name: logstash01\n    image: docker.rril.ril.com/logstash:7.7.1\n    ports:\n     - 5044:5044\n     - 9600:9600\n    volumes:\n     - ./beat.conf:/etc/logstash/conf.d/beat.conf\n     - ./logstash.yml:/usr/share/logstash/config/logstash.yml\n    restart: always\n    networks:\n      node_net:\n        ipv4_address: 172.28.1.4\n\n grafana:\n    container_name: grafana01\n    image: docker.rril.ril.com/grafana/grafana:8.0.6\n    ports:\n     - 3000:3000\n    volumes:\n     - ./grafana.ini:/etc/grafana/grafana.ini\n     - /mnt/grafana:/var/lib/grafana\n     - /logs/grafana:/var/log/grafana\n    restart: always\n    networks:\n      node_net:\n        ipv4_address: 172.28.1.5\n\n rabbitmq:\n    container_name: rabbitmq\n    hostname: rabbitmq\n    image: \"docker.rril.ril.com/rabbitmq:3.9.22-management-alpine\"\n    ports:\n     - \"5672:5672\"\n     - \"15672:15672\"\n     - \"15673:15673\"\n     - \"15675:15675\"\n    volumes:\n     - /application/rabbitmq:/var/lib/rabbitmq\n    environment:\n     - TZ=Asia/Kolkata\n     - RABBITMQ_DEFAULT_US